mapper.select:
select * from user

   
      and username like concat('%',#{username},'%')
   
   
      and role_id=#{roleId}
   
   
      and sex=#{sex}
   

 limit #{offset},#{pageSize}

select count(*) from user

   
      and username like concat('%',#{username},'%')
   
   
      and role_id=#{roleId}
   
   
      and sex=#{sex}
